import {
  Box,
  Center,
  Heading,
  Image,
  Link,
  Stack,
  Text,
} from '@chakra-ui/react';
import { ReactElement, ReactNode } from 'react';

import type { Image as ImageType, Training } from '../../../../shared/types';
import { baseImageUrl } from '../../axiosInstance/constants';

interface CardProps {
  itemName: string;
  image: ImageType;
  // cardContents: ReactNode;
  cardContents: any;
  // eslint-disable-next-line @typescript-eslint/ban-ts-comment
  trainings: any;
}
export function Card({
  itemName,
  image,
  cardContents,
  trainings = [],
}: CardProps): ReactElement {
  return (
    <Center py={12}>
      <Box
        p={6}
        maxW="330px"
        w="full"
        bg="white"
        boxShadow="2xl"
        rounded="lg"
        pos="relative"
        zIndex={1}
      >
        <Box rounded="lg" mt={-12} pos="relative" height="230px">
          <Image
            rounded="lg"
            height={230}
            width={282}
            objectFit="cover"
            src={`${baseImageUrl}/${image.fileName}`}
            alt={itemName}
          />
        </Box>
        <Stack pt={10}>
          <Heading align="center" fontSize="2xl">
            {itemName}
          </Heading>
          <ul>
            {trainings?.map(({ name }) => (
              <li
                key={name}
                style={{
                  textDecoration: cardContents?.includes(name?.toLowerCase())
                    ? 'none'
                    : 'line-through',
                }}
              >
                {name}
              </li>
            ))}
          </ul>

          {cardContents}
        </Stack>
      </Box>
    </Center>
  );
}
